$("#formAnalise").submit(function(e) {
$("#siteModal").modal("show");
    var url = "cadAnalise.php"; 
    $.ajax({
        type: "POST",
        url: url,
        data: $("#formAnalise").serialize(),
        success: function(data)
        {

            $("#siteModal").modal("hide");
            document.getElementById('formAnalise').reset();
            $("#siteModal2").modal("show");
        }
    });
    e.preventDefault();// esse comando serve para previnir que o form realmente realize o submit e atualize a tela.
});